;(function($) {
		
	$.fn.SuperBox = function(options) {
		var opts = $.extend({},{editText:'',deleteText:'',onEdit:function(id){},onDelete:function(id){}},options||{});
		var superbox      = $('<div class="superbox-show"></div>'),
			superboximg   = $('<img src="" class="superbox-current-img"><div id="imgInfoBox" class="superbox-imageinfo inline-block"> <h1>Image Title</h1><span><p><em>http://imagelink.com/thisimage.jpg</em></p><p class="superbox-img-description"></p><p><a href="javascript:void(0);" class="btn btn-primary btn-sm">'+opts.editText+'</a> <a href="javascript:void(0);" class="btn btn-danger btn-sm">'+opts.deleteText+'</a></p></span> </div>'),
			superboxclose = $('<div class="superbox-close txt-color-white"><i class="fa fa-times fa-lg"></i></div>');
		
		superbox.append(superboximg).append(superboxclose);
		
		return this.each(function(i,e) {
			var box = $(e);
			box.find('.superbox-list').click(function() {				
				$this = $(this);
				var currentimg = $this.find('.superbox-img'),
					imgData = currentimg.data('img'),
					imgDescription = currentimg.attr('alt') || "No description",
					imgLink = imgData,
					imgTitle = currentimg.attr('title') || "No Title";
					id       = currentimg.data('id') || 0
					
					
				superboximg.attr('src', imgData);
				
				$('.superbox-list').removeClass('active');
				$this.addClass('active');
				
				superboximg.find('em').text(imgLink);
				superboximg.find('>:first-child').text(imgTitle);
				superboximg.find('.superbox-img-description').text(imgDescription);
				
				if($('.superbox-current-img').css('opacity') == 0) {
					$('.superbox-current-img').animate({opacity: 1});
				}
				
				if ($(this).next().hasClass('superbox-show')) {
					$('.superbox-list').removeClass('active');
					superbox.toggle();
				} else {
					superbox.insertAfter(this).css('display', 'block');
					$this.addClass('active');
				}
				
				$('html, body').animate({
					scrollTop:superbox.position().top - currentimg.width()
				}, 'medium');
			
			});
						
			$('.superbox').on('click', '.superbox-close', function() {
				$('.superbox-list').removeClass('active');
				$('.superbox-current-img').animate({opacity: 0}, 200, function() {
					$('.superbox-show').slideUp();
				});
			});
			$('.superbox').on('click', '.btn-primary', function() {
				if($.isFunction(opts.onEdit)){
					opts.onEdit(id);
				}
			});
			$('.superbox').on('click', '.btn-danger', function() {
				if($.isFunction(opts.onDelete)){
					opts.onDelete(id);
				}
			});
		});
	};
})(jQuery);